"use client";

import React from "react";

function Grid({ width, height, step = 40 }: { width: number; height: number; step?: number }) {
  const verticalLines = Array.from({ length: Math.floor(width / step) + 1 }, (_, i) => i * step);
  const horizontalLines = Array.from({ length: Math.floor(height / step) + 1 }, (_, i) => i * step);
  return (
    <g>
      <rect x={0} y={0} width={width} height={height} fill="white" stroke="#E5E7EB" strokeWidth={1} />
      {verticalLines.map((x) => (
        <line key={`v-${x}`} x1={x} y1={0} x2={x} y2={height} stroke="#D1D5DB" strokeWidth={0.75} />
      ))}
      {horizontalLines.map((y) => (
        <line key={`h-${y}`} x1={0} y1={y} x2={width} y2={y} stroke="#D1D5DB" strokeWidth={0.75} />
      ))}
      <rect x={0} y={0} width={width} height={height} fill="none" stroke="#9CA3AF" strokeWidth={1.5} />
    </g>
  );
}

function SpacingGuides({ width, height }: { width: number; height: number }) {
  const pad = Math.min(width, height) * 0.06;
  return (
    <g>
      <rect x={pad} y={pad} width={width - pad * 2} height={height - pad * 2} fill="none" stroke="#9CA3AF" strokeDasharray="4 4" strokeWidth={1} />
      <rect x={pad * 2} y={pad * 2} width={width - pad * 4} height={height - pad * 4} fill="none" stroke="#BDBDBD" strokeDasharray="2 3" strokeWidth={0.75} />
      <circle cx={width / 2} cy={height / 2} r={Math.min(width, height) * 0.02} fill="#BDBDBD" />
    </g>
  );
}

